-- ----------------------------
-- Table structure for team_stats
-- ----------------------------
DROP TABLE IF EXISTS `team_stats`;
CREATE TABLE `team_stats`  (
  `id` int(0) NOT NULL AUTO_INCREMENT COMMENT '主键ID',
  `number` int(0) NOT NULL COMMENT '排名，对应JSON中的number',
  `team_name` varchar(50) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NULL DEFAULT NULL COMMENT '球队名称',
  `team` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NULL DEFAULT NULL,
  `matches` int(0) NOT NULL COMMENT '比赛场次',
  `wins` int(0) NOT NULL COMMENT '胜场',
  `draws` int(0) NOT NULL COMMENT '平局',
  `loses` int(0) NOT NULL COMMENT '负场',
  `goals` int(0) NOT NULL COMMENT '进球数',
  `ga` int(0) NOT NULL COMMENT '失球数',
  `points` int(0) NOT NULL COMMENT '积分',
  `xG` decimal(8, 2) NULL DEFAULT NULL COMMENT '预期进球',
  `NPxG` decimal(8, 2) NULL DEFAULT NULL COMMENT '非点球预期进球',
  `xGA` decimal(8, 2) NULL DEFAULT NULL COMMENT '预期失球',
  `NPxGA` decimal(8, 2) NULL DEFAULT NULL COMMENT '非点球预期失球',
  `NPxGD` decimal(8, 2) NULL DEFAULT NULL COMMENT '非点球预期净胜球',
  `ppda` decimal(8, 2) NULL DEFAULT NULL COMMENT '对方每防守动作允许的传球次数',
  `ppda_allowed` decimal(8, 2) NULL DEFAULT NULL COMMENT '己方每防守动作允许的传球次数',
  `deep` int(0) NOT NULL COMMENT '进攻三区完成次数',
  `deep_allowed` int(0) NOT NULL COMMENT '被对手在进攻三区完成次数',
  `xPTS` decimal(8, 2) NULL DEFAULT NULL COMMENT '预期积分',
  `goal_difference` int(0) GENERATED ALWAYS AS ((`goals` - `ga`)) STORED COMMENT '净胜球' NULL,
  `win_rate` decimal(5, 2) GENERATED ALWAYS AS (((`wins` * 100.0) / `matches`)) STORED COMMENT '胜率(%)' NULL,
  PRIMARY KEY (`id`) USING BTREE,
  UNIQUE INDEX `team`(`team_name`) USING BTREE,
  INDEX `idx_number`(`number`) USING BTREE,
  INDEX `idx_points`(`points`) USING BTREE,
  INDEX `idx_team`(`team_name`) USING BTREE,
  INDEX `idx_xpts_diff`(`xPTS`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 117 CHARACTER SET = utf8mb4 COLLATE = utf8mb4_0900_ai_ci COMMENT = '英超球队数据统计表' ROW_FORMAT = Dynamic;

